SELECT B.TITLE,B.BOARD_ID,R.REPLY_ID,R.WRITER_ID,R.CONTENTS,DATE_FORMAT(R.CREATED_DATE, '%Y-%m-%d') as CREATED_DATE FROM USED_GOODS_BOARD as B , USED_GOODS_REPLY as R WHERE B.BOARD_ID = R.BOARD_ID AND B.CREATED_DATE >= '2022-10-01' AND B.CREATED_DATE < '2022-11-01' ORDER BY CREATED_DATE, B.TITLE;